const Item = require("../models/item.model.js");

exports.create = (req, res) => {
  if (!req.body) {
    res.status(400).send({
      message: "Content can not be empty!"
    });
  }

  const item = new Item({
    name: req.body.name,
    qty: req.body.qty,
    amount: req.body.amount
  });

  Item.create(item, (err, data) => {
    if (err)
      res.status(500).send({
        message:
          err.message || "Error."
      });
    else res.send(data);
  });
};


exports.findAll = (req, res) => {
  Item.getAll((err, data) => {
    if (err)
      res.status(500).send({
        message:
          err.message || "error."
      });
    else res.send(data);
  });
};
