module.exports = app => {
  const items = require("../controllers/items.controller.js");

  // Create a new item
  app.post("/items", items.create);

  // read all items
  app.get("/items", items.findAll);

  // read a single item with itemId
  app.get("/items/:itemId", items.findOne);

  // Update an item with itemId
  app.put("/items/:itemId", items.update);

  // Delete an item with itemId
  app.delete("/items/:itemId", items.delete);

  // delete all
  app.delete("/items", items.deleteAll);
};